CREATE OR REPLACE FUNCTION public.resolution_enforce_no_winner_wash()
 RETURNS trigger
 LANGUAGE plpgsql
 SECURITY DEFINER
 SET search_path TO 'public'
AS $function$
DECLARE
  v_winners INTEGER;
BEGIN
  IF NEW.bet_status = 'resolved' AND NEW.winning_choice IS NOT NULL THEN
    SELECT COUNT(*) INTO v_winners
    FROM public.bet_participations bp
    WHERE bp.bet_id = NEW.bet_id
      AND bp.user_guess = NEW.winning_choice;

    IF COALESCE(v_winners, 0) = 0 THEN
      -- No winners; convert to washed before AFTER triggers fire
      NEW.bet_status := 'washed';
      NEW.winning_choice := NULL;
      NEW.resolution_time := now();

      INSERT INTO public.resolution_history(bet_id, event_type, payload)
      VALUES (NEW.bet_id, 'washed', jsonb_build_object('reason','resolved_no_winners'));
    END IF;
  END IF;
  RETURN NEW;
END;
$function$

CREATE OR REPLACE FUNCTION public.create_system_message_on_bet_washed()
 RETURNS trigger
 LANGUAGE plpgsql
 SECURITY DEFINER
 SET search_path TO 'public'
AS $function$DECLARE
  v_sys_id uuid;
  v_text   text;
BEGIN
  IF (OLD.bet_status IS DISTINCT FROM 'washed' AND NEW.bet_status = 'washed') THEN
    v_text := format('Bet %s washed', left(NEW.bet_id::text, 8));
    INSERT INTO public.system_messages (message_text, generated_at)
    VALUES (v_text, now())
    RETURNING system_message_id INTO v_sys_id;

    INSERT INTO public.feed_items (table_id, item_type, system_message_id, item_created_at)
    VALUES (NEW.table_id, 'system_message', v_sys_id, now());
  END IF;
  RETURN NEW;
END;$function$

CREATE OR REPLACE FUNCTION public.enforce_immutable_bet_participation_fields()
 RETURNS trigger
 LANGUAGE plpgsql
AS $function$
BEGIN
  IF NEW.bet_id <> OLD.bet_id OR NEW.table_id <> OLD.table_id OR NEW.user_id <> OLD.user_id THEN
    RAISE EXCEPTION 'bet_participations immutable fields (bet_id, table_id, user_id) cannot be modified';
  END IF;
  RETURN NEW;
END;
$function$

CREATE OR REPLACE FUNCTION public.set_bet_close_time()
 RETURNS trigger
 LANGUAGE plpgsql
AS $function$
BEGIN
  -- Derive close_time from proposal_time + time_limit_seconds
  NEW.close_time := NEW.proposal_time + make_interval(secs => NEW.time_limit_seconds::double precision);
  RETURN NEW;
END;
$function$

CREATE OR REPLACE FUNCTION public.create_system_message_on_bet_status_change()
 RETURNS trigger
 LANGUAGE plpgsql
 SECURITY DEFINER
 SET search_path TO 'public'
AS $function$DECLARE
  v_sys_id uuid;
  v_text   text;
  v_close_text text;
BEGIN
  IF (OLD.bet_status IS DISTINCT FROM NEW.bet_status) THEN
    -- active -> pending: include the close_time formatted to nearest second in ET (no date)
    IF (OLD.bet_status = 'active' AND NEW.bet_status = 'pending') THEN
      IF NEW.close_time IS NULL THEN
        v_close_text := 'unknown';
      ELSE
        v_close_text := to_char(
          (
            'epoch'::timestamptz
            + round(extract(epoch from NEW.close_time)) * interval '1 second'
          ) AT TIME ZONE 'America/New_York',
          'HH12:MI:SS AM'
        ) || ' ET';
      END IF;

      v_text := format(
        'Bet window for %s closed at %s',
        left(NEW.bet_id::text, 8),
        v_close_text
      );

      INSERT INTO public.system_messages (message_text, generated_at)
      VALUES (v_text, now())
      RETURNING system_message_id INTO v_sys_id;

      INSERT INTO public.feed_items (table_id, item_type, system_message_id, item_created_at)
      VALUES (NEW.table_id, 'system_message', v_sys_id, now());

    -- resolved: preserve existing resolved message format
    ELSIF (NEW.bet_status = 'resolved' AND OLD.bet_status IS DISTINCT FROM 'resolved') THEN
      v_text := format(
        'Bet %s resolved â€” winning choice: "%s"',
        left(NEW.bet_id::text, 8),
        COALESCE(NEW.winning_choice::text, 'unknown')
      );

      INSERT INTO public.system_messages (message_text, generated_at)
      VALUES (v_text, now())
      RETURNING system_message_id INTO v_sys_id;

      INSERT INTO public.feed_items (table_id, item_type, system_message_id, item_created_at)
      VALUES (NEW.table_id, 'system_message', v_sys_id, now());
    END IF;
  END IF;
  RETURN NEW;
END;$function$
